SELECT travel_berangkat.id_keberangkatan,travel_berangkat.`status`,travel_berangkat.keterangan, travel.id_travel,travel.jam_jemput as jamjemput,travel.tanggal_jemput,travel.nama,travel.hp,travel.alamat_jemput,travel.ket_antar,travel.rute,travel.seat,travel.jum_seat,travel.harga,travel.jenis,travel.jadwal_booking,travel.waktu_booking,travel.keterangan,travel.`status`,berangkat.id_berangkat,berangkat.tanggal_jemput,berangkat.jam_pulang,berangkat.jam_berangkat,dt_rekanan.id_rekan,dt_rekanan.nm_rekan as nm_oper,dt_rekanan.alamat_rekan as alamat_oper,dt_rekanan.no_hp as no_oper from travel_berangkat,travel,berangkat,dt_rekanan where travel.id_travel=travel_berangkat.id_travel and dt_rekanan.id_rekan=travel_berangkat.id_rekan and travel_berangkat.id_berangkat=berangkat.id_berangkat and travel.tanggal_jemput='$tanggal' and berangkat.id_berangkat='$id_berangkat'  AND travel_berangkat.keterangan='pergi'


SELECT sewa.id_sewa,sewa.tanggal_jemput,sewa.jam_jemput,sewa.nama_sewa,sewa.no_hp,sewa.alamat_jemput,sewa.alamat_antar,sewa.paket,sum(sewa.harga_sewa) as jumlah,sewa.harga_sewa,sewa.tanggal_booking,sewa.jam_booking,sewa.id_rekan,sewa_berangkat.id_sewaberangkat,sewa_berangkat.id_sewa,sewa_berangkat.id_berangkat,sewa_berangkat.id_rekan,sewa_berangkat.`status`,sewa_berangkat.keterangan,berangkat.id_supir,berangkat.id_mobil,berangkat.tanggal_jemput,berangkat.jam_pulang,berangkat.jam_berangkat,dt_rekanan.id_rekan as id_rekan_oper,dt_rekanan.nm_rekan as nm_rekan_oper from sewa,sewa_berangkat,berangkat,dt_rekanan WHERE sewa_berangkat.id_rekan=dt_rekanan.id_rekan and sewa_berangkat.id_berangkat=berangkat.id_berangkat and sewa.id_sewa=sewa_berangkat.id_sewa AND sewa_berangkat.id_berangkat='$id_berangkat' and sewa.tanggal_jemput='$tanggal' AND sewa_berangkat.keterangan='pergi' and sewa_berangkat.id_rekan = '1' group by sewa_berangkat.id_sewaberangkat